import logo from './logo.svg';
import './App.css';
import React from 'react';
import ReactDOM from 'react-dom/client';
import {
    RouterProvider,
  createBrowserRouter,
  createRoutesFromElements,
  Route,
  Outlet,
  Link,
    NavLink
} from "react-router-dom";
import MainApp from './App';
import SearchResultsPage from './SearchResultsPage';


function AskMeAnything(props) {

    const stylings = {
	alignItems: "center",
	textAlign: "center",
    }

    const whichGenre = () => {
	if (input = "action") {
	    document.h1.innerHTML('You want action? I\'ll give you action')
	    return;
	}
	else if ("romance") {
	    return <h1>Draw me like one of your french girls</h1>;
	}
	else if ("adventure") {
	    return <h1>So you're the adventurous type, are you?"</h1>;
	}
	else if ("sci-fi") {
	    return <h1>Beam me up Scotty</h1>;

	}
	else if ("western") {
	    return <h1>Yeeeee-haw y'all</h1>;
	}
	else if ("space opera") {
	    return <h1>See you later, space cowboy.</h1>;

	}

	else if ("theater" || "theatre" || "play" || "shakespeare") {
	    return <h1>Wherefore art thou, RO-ME-OH!!!!!! (a MLAATR reference and a shakespeare reference all in one!)</h1>;

	}
    }

    return(
	    <>
	    <title>Find a Good Book to Read</title>

		<div className="fuckaroundNFindOut" style={stylings}>
	    <h1>What genre of book would you like to read?</h1>
	    <label type="search" detail="search bar">
		<input type="search" />
	    </label>
		    <button type="search" text="search" title="Search For A Good Book To Read!" onClick={SearchResultsPage} target="_blank">Search</button>

		</div>
	    </>
    );
}

export default AskMeAnything;
