import logo from './logo.svg';
import './App.css';
import React from 'react';
import ReactDOM from 'react-dom/client';
import {
    createBrowserRouter,
    RouterProvider,
    Navigate,
} from "react-router-dom";
import MainApp from './App';


function AskMeAnything(props) {

    const stylings = {
	alignItems: "center",
	textAlign: "center",
    }

    const whichGenre = () => {
	if ("action") {
	    return <h1>"You want action? I/'ll give you action"</h1>;
	}
	else if ("romance") {
	    return "Draw me like one of your french girls";
	}
	else if ("adventure") {
	    return "So you're the adventurous type, are you?";
	}
	else if ("sci-fi") {
	    return "beam me up Scotty";

	}
	else if ("western") {
	    return "yeeeee-haw y'all";
	}
	else if ("space opera") {
	    return "see you later, space cowboy.";

	}

	else if ("theater" || "theatre" || "play" || "shakespeare") {
	    return "wherefore art thou, RO-ME-OH!!!!!! (a MLAATR reference and a shakespeare reference all in one!)";

	}
    }

    return(
	    <>
	    <head>
	    <title>Find a Good Book to Read</title>

	    </head>
		<div class="fuckaroundNFindOut" style={stylings}>
	    <h1>What genre of book would you like to read?</h1>
	    <label type="search" detail="search bar">
		<input type="search" />
	    </label>
		    <button type="search" text="search" title="Search For A Good Book To Read!" onClick={whichGenre}>Search</button>
		    </div>
	    </>
    );
}

export default AskMeAnything;
